//----------------------------------- our json msg format -----------------------------------//
//-the speed is faster than bson,but the length is more long(especially contain much number)-//


function json(){
   
}

json.prototype.encode = function(obj){
   
   var str = JSON.stringify(obj);
   
   return new Buffer(str,"utf8");   
};

json.prototype.decode = function(buf){
   
   var str = buf.toString("utf8");
   
   return JSON.parse(str); 
};

module.exports = json;