
//define const variables

const_v = module.exports;

//for log
const_v.LOG = {};
const_v.LOG.DEFAULT_LOG = "log";
const_v.LOG.ERR_LOG = "err";
const_v.LOG.MAX_KEY_LEN = 100;              //the max total key length of array or object,which can be log
const_v.LOG.DEFAULT_TIME = "0 0 * * *";     //every day change file
const_v.LOG.DEFAULT_SIZE = -1;              //ignore size
const_v.LOG.DEFAULT_FLINE = 1;              //log the file name and line number
const_v.LOG.DB_LOG = "db";
const_v.LOG.STOP_LOG = "stop";
const_v.LOG.HOOK_CONSOLE = 1;
const_v.LOG.FILE_FLUSHTIME = 1000;

//for system msg
const_v.SYS_MSG = {};
const_v.SYS_MSG.CONNECT = "_connect";
const_v.SYS_MSG.CLOSE = "_close";
const_v.SYS_MSG.HEARTBEAT = "_hb";
const_v.SYS_MSG.COMMON = "_common";

//for db
const_v.DB = {};
const_v.DB.DEFAULT_HOST = "/tmp/mongodb-27017.sock";    //we use unix socket
const_v.DB.DEFAULT_PORT = "27017";
const_v.DB.DEFAULT_NAME = "gamedb";
const_v.DB.DEFAULT_POOLSIZE = 3;
const_v.DB.DEFAULT_SURE = 1;                //ensure db request send ok (when db reconnect , send all no ack requests )
const_v.DB.MAX_REQ = 50;                    //the max num of requests per tick to send when db connected.
const_v.DB.RECONNECT_TIME = 5000;           //reconnect db time , millseconds

//for net
const_v.NET = {};
const_v.NET.AES_KEY = "bpgame server3.0 by faido";
//net msg head
const_v.NET.HEAD_JSON = 1;
const_v.NET.HEAD_BINARY = 2;
const_v.NET.HEAD_DEFAULT = const_v.NET.HEAD_BINARY;
//net msg body
const_v.NET.BODY_JSON = 1;
const_v.NET.BODY_BSON = 2;
const_v.NET.BODY_DEFAULT = const_v.NET.BODY_JSON;
//net msg crypto
const_v.NET.CRYPTO_DUMMY = 1;
const_v.NET.CRYPTO_AES = 2;
const_v.NET.CRYPTO_DEFAULT = const_v.NET.CRYPTO_DUMMY;
//for net info
const_v.NET.TIMEOUT = 30000;                  //server socket timeout to close.
const_v.NET.TIMEOUT_HEATBEAT = 20000;         //client socket timeout to send heartbeat.
const_v.NET.DEFAULT_BACKLOG = 1023;           //the kernel does: backlogsize = roundup_pow_of_two(backlogsize + 1),so we use 1023 as backlog
const_v.NET.DEFAULT_RETRY = 1;                //client retry to connect to server when closed.
const_v.NET.DEFAULT_RETRYTIME = 3000;         //client retry to connect after 3 secs.
const_v.NET.DEFAULT_MSGID = "_id";            //default msg id key.
const_v.NET.RECV_MSGSIZE = 2097152;           //max recv msg size,avoid client send invalid msg to increase buffer size.

//for server
const_v.SERVER = {};
const_v.SERVER.STOP_TIME = 300000;            //stop delay time, let log cache to file and db finish
const_v.SERVER.HOOK_TIMER = 1;                //hook timer when in debug

